function quicksort(arr) {
  if (arr.length < 2) {
    //
    return arr;
  } else {
    let pivot = arr[0]; //
    let less = [],
      greater = [];
    //
    arr.forEach(item => {
      if (item < pivot) {
        less.push(item);
      }
      if (item > pivot) {
        greater.push(item);
      }
    });
    return [...quicksort(less), pivot, ...quicksort(greater)];
  }
}
```

